import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import axios from "axios";
import { FaUserCircle, FaSignOutAlt, FaChevronDown } from "react-icons/fa";
import styles from "./Dashboard.css"; // Adjust path if needed

const Dashboard = () => {
  const [user, setUser] = useState(null); // Store user data
  const [showMenu, setShowMenu] = useState(false); // Toggle menu visibility
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        navigate("/login"); // If no token, redirect to login
        return;
      }

      try {
        const response = await axios.get("http://localhost:8000/dashboard", {
          headers: {
            Authorization: `Bearer ${token}`, // Send token in Authorization header
          },
        });

        if (response.data.success) {
          setUser(response.data); // Set the user data if request is successful
        } else {
          toast.error(response.data.message || "Failed to fetch user data.");
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
        toast.error(
          error.response?.data?.message || "An error occurred during fetch."
        );
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token on logout
    navigate("/login");
  };

  const handleNavigate = (path) => {
    navigate(path);
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <FaUserCircle className={styles.icon} />
        <span className={styles.username}>
          {user ? user.message : "Loading..."} {/* Show user data or loading */}
        </span>
        <FaChevronDown
          className={styles.menuIcon}
          onClick={() => setShowMenu(!showMenu)}
        />
      </div>

      {showMenu && (
        <div className={styles.dropdownMenu}>
          <p onClick={() => handleNavigate("/profile")}>Profile</p>
          <p onClick={() => handleNavigate("/settings")}>Settings</p>
          <p onClick={handleLogout}>Logout</p>
        </div>
      )}

      <ToastContainer />
    </div>
  );
};

export default Dashboard;
